import React, { useState } from 'react';
import axios from 'axios';

const QrGenerator = () => {
  const [count, setCount] = useState(1);
  const [results, setResults] = useState([]);

  const generateQR = async () => {
    try {
      const res = await axios.post("http://localhost:5000/api/generate_qr", {
        count: parseInt(count, 10)
      });
      setResults(res.data);
    } catch (error) {
      console.error("Erreur lors de la génération des QR codes :", error);
    }
  };

  return (
    <div style={{ marginBottom: '40px' }}>
      <h2>Générateur de QR Codes</h2>
      <input
        type="number"
        min="1"
        value={count}
        onChange={e => setCount(e.target.value)}
      />
      <button onClick={generateQR}>Générer</button>

      <div>
        {results.map(qr => (
          <div key={qr.code} style={{ margin: '10px 0' }}>
            <p>{qr.code}</p>
            <img
              src={`http://localhost:5000${qr.image_path}`}
              alt="QR Code"
              width="150"
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default QrGenerator;
