import React, { useState } from 'react';
import axios from 'axios';

const QrGenerator = () => {
  const [count, setCount] = useState(1);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  const generateQR = async () => {
    const numCount = parseInt(count, 10);
    if (isNaN(numCount) || numCount < 1) {
      setErrorMsg("Veuillez entrer un nombre valide (≥ 1).");
      return;
    }

    setErrorMsg("");
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5000/api/generate_qr", { count: numCount });
      setResults(res.data);
    } catch (error) {
      console.error("Erreur lors de la génération des QR codes :", error);
      setErrorMsg("Erreur lors de la génération des QR codes. Veuillez réessayer.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ marginBottom: '20px' }}>Générateur de QR Codes</h2>
      <div style={inputContainerStyle}>
        <input
          type="number"
          min="1"
          value={count}
          onChange={e => setCount(e.target.value)}
          style={inputStyle}
          aria-label="Nombre de QR codes à générer"
        />
        <button
          onClick={generateQR}
          disabled={loading}
          style={buttonStyle}
          aria-busy={loading}
        >
          {loading ? "Génération..." : "Générer"}
        </button>
      </div>

      {errorMsg && <p style={errorStyle}>{errorMsg}</p>}

      <div style={resultsContainerStyle}>
        {results.map(qr => (
          <div key={qr.code} style={qrItemStyle}>
            <p>{qr.code}</p>
            <img
              src={`http://localhost:5000${qr.image_path}`}
              alt={`QR code pour ${qr.code}`}
              width="150"
              height="150"
              style={{ border: '1px solid #ccc', borderRadius: '8px' }}
            />
          </div>
        ))}
      </div>
    </div>
  );
};

const containerStyle = {
  maxWidth: '600px',
  margin: '0 auto 40px',
  padding: '20px',
  backgroundColor: '#f9faff',
  borderRadius: '10px',
  boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
  textAlign: 'center',
};

const inputContainerStyle = {
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  gap: '10px',
  marginBottom: '15px',
};

const inputStyle = {
  width: '80px',
  padding: '8px',
  fontSize: '16px',
  borderRadius: '6px',
  border: '1px solid #ccc',
  textAlign: 'center',
};

const buttonStyle = {
  padding: '8px 16px',
  fontSize: '16px',
  borderRadius: '6px',
  border: 'none',
  backgroundColor: '#007bff',
  color: '#fff',
  cursor: 'pointer',
  transition: 'background-color 0.3s',
};

const errorStyle = {
  color: 'red',
  marginBottom: '15px',
};

const resultsContainerStyle = {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'center',
  gap: '20px',
  marginTop: '20px',
};

const qrItemStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  minWidth: '150px',
};

export default QrGenerator;
