import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QuestionForm = () => {
  const [text, setText] = useState('');
  const [questions, setQuestions] = useState([]);
  const [selected, setSelected] = useState({});
  const [message, setMessage] = useState('');

  // Charger les questions depuis l'API Flask
  const fetchQuestions = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/questions');
      setQuestions(res.data);
      setSelected({});
    } catch (error) {
      setMessage('Erreur lors du chargement des questions.');
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  // Ajouter une question
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/questions', { text });
      setMessage('✅ Question ajoutée avec succès !');
      setText('');
      fetchQuestions();
    } catch (error) {
      setMessage('❌ Erreur lors de l\'ajout.');
    }
  };

  // Gérer la sélection des checkboxes
  const toggleSelect = (id) => {
    setSelected((prev) => ({
      ...prev,
      [id]: !prev[id],
    }));
  };

  // Supprimer les questions sélectionnées (appel DELETE pour chaque question)
  const deleteSelected = async () => {
    const idsToDelete = Object.entries(selected)
      .filter(([_, isChecked]) => isChecked)
      .map(([id]) => id);

    if (idsToDelete.length === 0) {
      setMessage('Veuillez sélectionner au moins une question à supprimer.');
      return;
    }

    try {
      // Suppression en parallèle avec la bonne URL incluant /api
      await Promise.all(
        idsToDelete.map((id) =>
          axios.delete(`http://localhost:5000/api/delete_question/${id}`)
        )
      );
      setMessage(`✅ ${idsToDelete.length} question(s) supprimée(s) avec succès.`);
      fetchQuestions();
    } catch (error) {
      setMessage('Erreur lors de la suppression des questions.');
    }
  };

  return (
    <div style={containerStyle}>
      <h2>Ajouter une Question</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <textarea
          rows={4}
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Écris ta question ici"
          style={textareaStyle}
          required
        />
        <button type="submit" style={buttonStyle}>
          Ajouter
        </button>
      </form>

      <h2 style={{ marginTop: '40px' }}>Liste des Questions</h2>
      {message && <p style={messageStyle}>{message}</p>}

      <button
        onClick={deleteSelected}
        style={{ ...buttonStyle, marginBottom: '15px', backgroundColor: '#dc3545' }}
      >
        Supprimer la sélection
      </button>

      <ul style={listStyle}>
        {questions.map(({ id, text }) => (
          <li key={id} style={listItemStyle}>
            <input
              type="checkbox"
              checked={!!selected[id]}
              onChange={() => toggleSelect(id)}
              style={{ marginRight: '10px' }}
              aria-label={`Sélectionner la question ${id}`}
            />
            <span>{text}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

const containerStyle = {
  maxWidth: '600px',
  margin: '20px auto',
  fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  padding: '20px',
  backgroundColor: '#f9faff',
  borderRadius: '8px',
  boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '15px',
};

const textareaStyle = {
  padding: '10px',
  fontSize: '16px',
  borderRadius: '8px',
  border: '1.5px solid #ccc',
  resize: 'vertical',
  minHeight: '100px',
  fontFamily: 'inherit',
};

const buttonStyle = {
  backgroundColor: '#007bff',
  color: '#fff',
  fontWeight: 'bold',
  border: 'none',
  padding: '12px',
  borderRadius: '8px',
  cursor: 'pointer',
  fontSize: '16px',
  transition: 'background-color 0.3s',
};

const messageStyle = {
  fontWeight: 'bold',
  color: '#333',
};

const listStyle = {
  listStyleType: 'none',
  padding: 0,
  maxHeight: '300px',
  overflowY: 'auto',
  borderTop: '1px solid #ddd',
};

const listItemStyle = {
  padding: '10px',
  borderBottom: '1px solid #ddd',
  display: 'flex',
  alignItems: 'center',
};

export default QuestionForm;
