import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QuestionForm = () => {
  const [text, setText] = useState('');
  const [questions, setQuestions] = useState([]);
  const [selected, setSelected] = useState({});
  const [message, setMessage] = useState('');
  const [editId, setEditId] = useState(null);      // id de la question en édition
  const [editText, setEditText] = useState('');    // texte modifié

  // Charger les questions depuis l'API Flask
  const fetchQuestions = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/questions');
      setQuestions(res.data);
      setSelected({});
    } catch (error) {
      setMessage('Erreur lors du chargement des questions.');
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  // Ajouter une question
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/questions', { text });
      setMessage('✅ Question ajoutée avec succès !');
      setText('');
      fetchQuestions();
    } catch (error) {
      setMessage('❌ Erreur lors de l\'ajout.');
    }
  };

  // Supprimer une question par son ID
  const deleteQuestion = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/delete_question/${id}`);
      setMessage('✅ Question supprimée avec succès.');
      fetchQuestions();
    } catch (error) {
      setMessage('❌ Erreur lors de la suppression.');
    }
  };

  // Démarrer l'édition d'une question
  const startEditing = (id, currentText) => {
    setEditId(id);
    setEditText(currentText);
    setMessage('');
  };

  // Annuler l'édition
  const cancelEditing = () => {
    setEditId(null);
    setEditText('');
  };

  // Sauvegarder la modification
  const saveEdit = async () => {
    if (!editText.trim()) {
      setMessage('Le texte ne peut pas être vide.');
      return;
    }
    try {
      await axios.put(`http://localhost:5000/api/update_question/${editId}`, { text: editText });
      setMessage('✅ Question modifiée avec succès.');
      setEditId(null);
      setEditText('');
      fetchQuestions();
    } catch (error) {
      setMessage('❌ Erreur lors de la modification.');
    }
  };

  return (
    <div style={containerStyle}>
      <h2>Ajouter une Question</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <textarea
          rows={4}
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Écris ta question ici"
          style={textareaStyle}
          required
        />
        <button type="submit" style={buttonStyle}>Ajouter</button>
      </form>

      <h2 style={{ marginTop: '40px' }}>Liste des Questions</h2>
      {message && <p style={messageStyle}>{message}</p>}

      <ul style={listStyle}>
        {questions.map(({ id, text }) => (
          <li key={id} style={listItemStyle}>
            {/* Edition */}
            {editId === id ? (
              <>
                <textarea
                  rows={2}
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  style={{ ...textareaStyle, minHeight: '50px', marginRight: '10px', flex: 1 }}
                />
                <button onClick={saveEdit} style={{ ...buttonStyle, marginRight: '5px', backgroundColor: '#28a745' }}>
                  💾
                </button>
                <button onClick={cancelEditing} style={{ ...buttonStyle, backgroundColor: '#6c757d' }}>
                  ✖️
                </button>
              </>
            ) : (
              <>
                <span style={{ flex: 1 }}>{text}</span>

                {/* Bouton éditer */}
                <button
                  onClick={() => startEditing(id, text)}
                  style={{ ...iconButtonStyle, marginRight: '10px' }}
                  aria-label={`Éditer la question ${id}`}
                  title="Éditer"
                >
                  ✏️
                </button>

                {/* Bouton supprimer */}
                <button
                  onClick={() => {
                    if(window.confirm("Voulez-vous vraiment supprimer cette question ?")) {
                      deleteQuestion(id);
                    }
                  }}
                  style={iconButtonStyle}
                  aria-label={`Supprimer la question ${id}`}
                  title="Supprimer"
                >
                  🗑️
                </button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

// Styles...

const containerStyle = {
  maxWidth: '600px',
  margin: '20px auto',
  fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  padding: '20px',
  backgroundColor: '#f9faff',
  borderRadius: '8px',
  boxShadow: '0 2px 10px rgba(0,0,0,0.05)',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '15px',
};

const textareaStyle = {
  padding: '10px',
  fontSize: '16px',
  borderRadius: '8px',
  border: '1.5px solid #ccc',
  resize: 'vertical',
  minHeight: '100px',
  fontFamily: 'inherit',
};

const buttonStyle = {
  backgroundColor: '#007bff',
  color: '#fff',
  fontWeight: 'bold',
  border: 'none',
  padding: '12px',
  borderRadius: '8px',
  cursor: 'pointer',
  fontSize: '16px',
  transition: 'background-color 0.3s',
};

const iconButtonStyle = {
  backgroundColor: 'transparent',
  border: 'none',
  cursor: 'pointer',
  fontSize: '20px',
  padding: '0 5px',
};

const messageStyle = {
  fontWeight: 'bold',
  color: '#333',
};

const listStyle = {
  listStyleType: 'none',
  padding: 0,
  maxHeight: '300px',
  overflowY: 'auto',
  borderTop: '1px solid #ddd',
};

const listItemStyle = {
  padding: '10px',
  borderBottom: '1px solid #ddd',
  display: 'flex',
  alignItems: 'center',
};

export default QuestionForm;
