import React, { useState } from 'react';

const links = [
  { label: 'Accueil', href: '#' },
  { label: 'QR Codes', href: '#qr' },
  { label: 'Questions', href: '#questions' },
  { label: 'Contact', href: '#contact' },
];

const Toolbar = () => {
  // On stocke l’index du lien survolé, -1 sinon
  const [hovered, setHovered] = useState(-1);

  const toolbarStyle = {
    width: '100%',
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '15px 20px',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    position: 'sticky',
    top: 0,
    zIndex: 1000,
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    fontSize: '20px',
  };

  const titleStyle = {
    fontWeight: 'bold',
    letterSpacing: '2px',
    marginBottom: '10px',
  };

  const navStyle = {
    display: 'flex',
    gap: '20px',
    justifyContent: 'center',
    flexWrap: 'wrap',
  };

  const baseLinkStyle = {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
    transition: 'color 0.3s',
    cursor: 'pointer',
  };

  const hoverColor = '#ffdd57'; // Couleur au survol

  return (
    <div style={toolbarStyle}>
      <div style={titleStyle}>SEZAIA</div>
      <nav style={navStyle}>
        {links.map((link, i) => (
          <a
            key={link.label}
            href={link.href}
            style={{
              ...baseLinkStyle,
              color: hovered === i ? hoverColor : baseLinkStyle.color,
            }}
            onMouseEnter={() => setHovered(i)}
            onMouseLeave={() => setHovered(-1)}
          >
            {link.label}
          </a>
        ))}
      </nav>
    </div>
  );
};

export default Toolbar;
