import React, { useState } from 'react';

const items = ['Accueil', 'QR Codes', 'Questions', 'Contact'];

const Toolbar = () => {
  const [hovered, setHovered] = useState(-1);

  const toolbarStyle = {
    width: '100%',
    backgroundColor: '#007bff',
    color: '#fff',
    padding: '15px 20px',
    boxShadow: '0 2px 4px rgba(0,0,0,0.2)',
    position: 'sticky',
    top: 0,
    zIndex: 1000,
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    fontSize: '20px',
    userSelect: 'none',
  };

  const titleStyle = {
    fontWeight: 'bold',
    letterSpacing: '3px',
    marginBottom: '12px',
  };

  const navStyle = {
    display: 'flex',
    gap: '25px',
    justifyContent: 'center',
    flexWrap: 'wrap',
  };

  const itemStyle = (isHovered) => ({
    color: isHovered ? '#ffdd57' : '#fff',
    fontSize: '16px',
    transition: 'color 0.3s ease',
    cursor: 'default',
  });

  return (
    <div style={toolbarStyle}>
      <div style={titleStyle}>SEZAIA</div>
      <nav style={navStyle}>
        {items.map((item, i) => (
          <span
            key={item}
            style={itemStyle(hovered === i)}
            onMouseEnter={() => setHovered(i)}
            onMouseLeave={() => setHovered(-1)}
          >
            {item}
          </span>
        ))}
      </nav>
    </div>
  );
};

export default Toolbar;
