import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import Toolbar from './components/Toolbar';
import QuestionForm from './components/QuestionForm';
import QrGenerator from './components/QrGenerator';

function App() {
  return (
    <Router>
      <Toolbar />
      <nav style={{ padding: '10px', textAlign: 'center', backgroundColor: '#eee' }}>
        <Link to="/qr-generator" style={linkStyle}>Générateur QR</Link> |{' '}
        <Link to="/questions" style={linkStyle}>Questions</Link>
      </nav>
      <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
        <Routes>
          <Route path="/" element={<Navigate to="/qr-generator" replace />} />
          <Route path="/qr-generator" element={<QrGenerator />} />
          <Route path="/questions" element={<QuestionForm />} />
        </Routes>
      </div>
    </Router>
  );
}

const linkStyle = {
  margin: '0 10px',
  textDecoration: 'none',
  color: '#007bff',
};

export default App;
